import { Component, OnInit } from '@angular/core';
import { Cosmetique } from '../model/Cosmetique.model'; // Changed import
import { Cosmetique } from '../services/Cosmetique.service'; // Changed import
import { Classification } from '../model/classification.model';

@Component({
  selector: 'app-recherche-par-nom',
  templateUrl: './recherche-par-nom.component.html'
})
export class RechercheParNomComponent implements OnInit {

  cosmetiques!: Cosmetique[]; // Changed type
  nomCosmetique!: string; // Changed variable name
  classifications !: Classification[];
  searchTerm!: string;
  AllCosmetique! : Cosmetique[]; // Changed type

  constructor(private cosmetiqueService: CosmetiqueService) {} // Changed service reference

  ngOnInit(): void {
    this.cosmetiqueService.listeCosmetiques().subscribe(cosmetiques => { 
      console.log(cosmetiques);
      this.cosmetiques = cosmetiques;
    });
  }

  rechercherCosmetique() { // Changed method name
    this.cosmetiqueService.rechercherParNom(this.nomCosmetique).subscribe(cosmetiques => { 
      this.cosmetiques = cosmetiques; 
      console.log(cosmetiques);
    });
  }

  onKeyUp(filterText: string) {
    this.cosmetiques = this.AllCosmetique.filter(item =>
      item.nomCosmetique.toLowerCase().includes(filterText.toLowerCase())); // Changed property access
  }
}
