import { Classification } from './../model/classification.model';
import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
import { ArticleService } from '../services/article.service';

@Component({
  selector: 'app-recherche-par-classtfication',
  templateUrl: './recherche-par-classtfication.component.html',
})
export class RechercheParClasstficationComponent implements OnInit {
  cosmetiques!: article[];
  IdClassification!: number;
  classifications!: Classification[];
  NomClassification!: string ;

  constructor(private articleService: ArticleService) {}

  ngOnInit(): void {
    this.articleService.listeclassifications().subscribe(response => {
      this.classifications = response;
      console.log(this.classifications);
    });
  }

  onChange() {
    if (this.IdClassification !== undefined && !isNaN(this.IdClassification)) {
      this.articleService.rechercheParClasstfication(this.IdClassification)
        .subscribe(arts => {
          this.cosmetiques = arts;
        });
    } else {
      console.error('IdClassification is not a valid number. Cannot make the request.');
    }
  }


}






<html>
    <div class="container">
          <div class="col-sm-4 col-md-4 col-lg-4 mb-4">
            <label>Classification</label>
            <select class="form-control form-control-lg" id="nomClas" name="nomClas" formControlName="NomClassification" (change)="onChange()">
              <option *ngFor="let element of classifications" [value]="element.idClas">
                {{element.nomClas}}
              </option>
            </select>
          </div>      
        <div class="card shadow mb-4">
          <div class="card-body">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nom</th>
                  <th>Prix</th>
                  <th>Date Création</th>
                  <th>Classification</th>
                </tr>
              </thead>
              <tbody *ngFor="let element of cosmetiques">
                <tr>
                  <td>{{element.idarticle}}</td>
                  <td>{{element.nomarticle}}</td>
                  <td>{{element.prixarticle}}</td>
                  <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                  <td>{{element.classification?.nomClas}}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
    </div>

</html>
