import { Component, OnInit } from '@angular/core';
import { AuthService } from './services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  title = 'cosmetique';
  constructor (public authService: AuthService,
                private router:Router) {}


  ngOnInit() {
    let isloggedin: string = localStorage.getItem('isloggedIn') || '';
    let loggedUser: string = localStorage.getItem('loggedUser') || '';

    if (isloggedin !== 'true' || !loggedUser) {
      this.router.navigate(['/login']);
    } else {
      this.authService.setLoggedUserFromLocalStorage(loggedUser);
    }
  }


  onLogout()
  {
    this.authService.logout();
  }
}
<!DOCTYPE html>
<html >
  <head>
    <meta charset="utf-8">
    <title>Gestion des Article cosmétique</title>
  </head>
  <body>
    <nav class="navbar navbar-expand-sm bg-dark navbar-dark">
      <!-- Brand -->
      <a class="navbar-brand" href="#">Gestion des article cosmétique</a>
      <!-- Links -->
      <ul class="navbar-nav">
        <li class="nav-item">
          <a class="nav-link"  routerLink="/cosmetiques" >Home</a>
        </li>
        <!-- Dropdown -->
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" id="navbardrop" data-toggle="dropdown">cosmétique</a>
          <div class="dropdown-menu">
            <a *ngIf= "authService.isAdmin()" class="dropdown-item" routerLink = "add-cosmetique">Ajouter</a>
            <a class="dropdown-item" href="#">Lister</a>
            <a class="dropdown-item" routerLink="/rechercheParCategorie" >Recherche Par Catégorie</a>
            <a class="dropdown-item" routerLink="/rechercheParNom" >Recherche Par Nom</a>
          </div>
        </li>
      </ul>
      <ul class="navbar-nav ml-auto" >
        <li> <a class="nav-link">{{authService.loggedUser}}</a></li>
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" id="navbardrop" data-toggle="dropdown">Connexion</a>
          <div class="dropdown-menu">
            <a *ngIf= "!authService.isloggedIn" class="dropdown-item" routerLink = "login">login</a>
            <a *ngIf="authService.isloggedIn" class="dropdown-item" (click) = "onLogout()"> logout</a>
            <a class="dropdown-item" href="#">Profile</a>
          </div>
        </li>
      </ul>
    </nav>
  </body>
</html>
