import { ArticleService  } from '../services/article.service';
import { Classification } from '../model/classification.model';
import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';

@Component({
  selector: 'app-recherche-par-classtfication',
  templateUrl: './recherche-par-classtfication.component.html',
})
export class RechercheParClasstficationComponent implements OnInit {

  cosmetiques!: article[];
  IdClassification!: number;
  classifications!: Classification[];
  NomClassification: string ='';

  constructor(private articleService: ArticleService) {}

  ngOnInit(): void {
    this.classifications = this.articleService.listeclassifications();
    console.log(this.classifications);
  }
  onChange(): void {
    if (this.IdClassification) {
        const classification = this.articleService.consulterclassification(this.IdClassification);

        if (classification) {
            this.cosmetiques = this.articleService.RechercheParClasstfication(classification.idClas);
            console.log('Produits après recherche :', this.cosmetiques);
        } else {
            console.error('Classification non trouvée pour l\'ID :', this.IdClassification);
        }
    } else {
        this.cosmetiques = [];
    }
}


