      this.cosmetiques = this.articleService.RechercheParClasstfication(idClas);
      console.log('Produits après recherche :', this.cosmetiques);
    } else {
      this.cosmetiques = [];
    }
  }
   */

}







<html>
    <div class="container">

        <div class="col-sm-4 col-md-4 col-lg-4 mb-4">
            <label>Categories </label>
            <select class="form-control form-control-lg" id="nomClas" name="nomClas" [(ngModel)]="NomClassification" (change)="onChange()">

                <option *ngFor="let element of classifications" value="{{element.idClas}}">
                    {{element.nomClas}}
                </option>
            </select>
        </div>
        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped"> 
                    <thead>
                        <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th><th>type</th> </tr>
                    </thead>
                    <tbody *ngFor="let element of cosmetiques">
                        <tr *ngIf="element.Classification">
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.Classification.nomClas}}</td>
                            <!--  <td><a class="btn btn-danger" (click)= "supprimerArticle_cosmetique(element)">Supprimer</a></td>
                            <td><a  class="btn btn-success" [routerLink]="['/update-article-cosmetique',element.idarticle]">Modifier</a></td> -->
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
