import { Component, OnInit } from '@angular/core';
import { Cosmetique } from '../model/Cosmetique.model'; // Changed import
import { ActivatedRoute, Router } from '@angular/router';
import { CosmetiqueService } from '../services/Cosmetique.service'; // Changed import
import { Classification } from "../model/classification.model";

@Component({
  selector: 'app-update-cosmetique', // Changed selector
  templateUrl: './update-cosmetique.component.html' // Changed templateUrl
})
export class UpdateCosmetiqueComponent implements OnInit { // Changed component name and implemented OnInit

  currentCosmetique = new Cosmetique(); // Changed object type
  classifications!: Classification[]; // Changed variable type
  updatedClasId!: number;

  constructor(
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private cosmetiqueService: CosmetiqueService // Changed service name
  ) { }

  ngOnInit(): void {
    this.cosmetiqueService.listerClassifications().subscribe(cats => {
      this.classifications = cats;
      console.log(cats);
    });
    this.cosmetiqueService.consulterCosmetique(this.activatedRoute.snapshot.params['id']).subscribe(cosmetique => {
      this.currentCosmetique = cosmetique;
      this.updatedClasId = this.currentCosmetique.classification.idClas;
    });
  }

  updateCosmetique() { // Changed method name
    this.currentCosmetique.classification = this.classifications.find(clas => clas.idClas == this.updatedClasId)!;
    this.cosmetiqueService.updateCosmetique(this.currentCosmetique).subscribe(prod => {
      this.router.navigate(['cosmetiques']);
    });
  }
}
<html>
    <div class="container">
        <div>
            <h2>Modifier un Cosmetique :</h2> <!-- Changed heading -->
        </div>
        <form  (ngSubmit)="updateCosmetique()"> <!-- Changed method name -->
            <div class="col-sm-2 col-md-2 col-lg-2">
                <label >ID Cosmetique</label> <!-- Changed label -->
                <input  readonly type="text" [(ngModel)]= "currentCosmetique.idCosmetique" name="idCosmetique" class="form-control"> <!-- Changed property name -->
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4" >
                <label >Nom Cosmetique</label> <!-- Changed label -->
                <input type="text"[(ngModel)]= "currentCosmetique.nomCosmetique" name="nomCosmetique" class="form-control"> <!-- Changed property name -->
            </div>
            <div class="col-sm-2 col-md-2 col-lg-2">
                <label >Prix Cosmetique</label> <!-- Changed label -->
                <input type="number" [(ngModel)]= "currentCosmetique.prixCosmetique" name="prixCosmetique" class="form-control"> <!-- Changed property name -->
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4">
                <label >Date création</label> <!-- Changed label -->
                <input type="date" [ngModel]= "currentCosmetique.dateCreation | date: 'yyyy-MM-dd'" (ngModelChange)= " currentCosmetique.dateCreation= $event" name="dateCreation"class="form-control"> <!-- Changed property name -->
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4">
                <label >Classification</label> <!-- Changed label -->
                <select class="form-control form-control-lg" id="idCat" name="idCat" [(ngModel)]="updatedClasId">
                    <option *ngFor="let clas of classifications" [value]="clas.idClas"> <!-- Changed property name -->
                        {{clas.nomClas}}
                    </option>
                </select>
            </div>
            <div>
                <button type="submit" class="btn btn-success">Modifer</button> <!-- Changed method call -->
            </div>
        </form>
    </div>
</html>
