import { Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
import { ArticleService  } from '../services/article.service';
import { AuthService } from '../services/auth.service';
@Component({
  selector: 'app-cosmetiques',
  templateUrl: './cosmetiques.component.html'
})
export class CosmetiquesComponent implements OnInit {

  cosmetiques !: article[];


  constructor(private articleService : ArticleService,
              public authService: AuthService,
              private router:Router) { 
          this.chargerArticle_cosmetiques();

              }
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }


  chargerArticle_cosmetiques(){
    this.articleService.listeArticleCosmetique().subscribe(arts => {
    console.log(arts);
    this.cosmetiques= arts;
    }); 
    }
  supprimerArticle_cosmetique(A: article) {
    let conf = confirm("Etes-vous sûr ?");
    if (conf)
      this.articleService.supprimerArticle_cosmetique(A.idarticle).subscribe(() => {
        console.log("article cosmeti supprimé");
        this.chargerArticle_cosmetiques();
      });
  }
}
<html>
    <div class="container">
        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped">

                    <thead>
                        <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th><th>Classification</th> </tr>
                    </thead>
                    <tbody *ngFor= "let element of cosmetiques">
                        <tr >
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.Classification?.nomClas}}</td>
                            <td><a *ngIf= "authService.isAdmin()"class="btn btn-danger" (click)="supprimerArticle_cosmetique(element)">Supprimer</a></td>
                            <td><a *ngIf= "authService.isAdmin()" class="btn btn-success" [routerLink]="['/update-article-cosmetique',element.idarticle]">Modifier</a></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
