import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
@Component({
  selector: 'app-cosmetiques',
  templateUrl: './cosmetiques.component.html'
})
export class CosmetiquesComponent implements OnInit {

  cosmetiques : article[];
  constructor() {
    this.cosmetiques  = ["SUN SECURE Blur SPF50","SVR sebiaclear gel moussant 200 ml","SVR hydraliane crème hydratante riche 40 Ml"];
    }
  ngOnInit(): void {

  }
}
<html>
    <div class="container">
        <div class="card shadow mb-4">
            <div class="card-body">
                <table >
                    <thead>
                        <tr>
                            <th>Liste de Produits</th>
                        </tr>
                    </thead>
                    <tbody *ngFor="let element of cosmetiques">
                        <tr >
                            <td>{{element}}</td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
