import { Component } from '@angular/core';

@Component({
  selector: 'app-recherche-par-classtfication',
  templateUrl: './recherche-par-classtfication.component.html',
})
export class RechercheParClasstficationComponent {

}
<div class="col-sm-4 col-md-4 col-lg-4 mb-4">
    <label>Categories </label>
    <select class="form-control form-control-lg" id="idClas" name="idClas" [(ngModel)]= "idarticle" (change)= "onChange()">
        <option *ngFor= "let clas of categories" value="{{clas.idClas}}">
            {{clas.nomClas}}
        </option>
    </select>
</div>
