import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
import { ArticleService  } from '../services/article.service';
@Component({
  selector: 'app-cosmetiques',
  templateUrl: './cosmetiques.component.html'
})
export class CosmetiquesComponent implements OnInit {

  cosmetiques : article[];

  constructor(private ArticleService: ArticleService  ) {
  this.cosmetiques = ArticleService.listeArticleCosmetique();

  }
  supprimerArticle_cosmetique(A :article)
  {
    //console.log(A);

    let conf = confirm("Etes-vous sûr ?");
    if (conf)
      this.ArticleService.supprimerArticle_cosmetique(A);


  }
  ngOnInit(): void {

  }

}
<html>
    <div class="container">
        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped">

                    <thead>
                        <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th><th>Classe</th> </tr>
                    </thead>
                    <tbody *ngFor= "let element of cosmetiques">
                        <tr >
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.classification.nomClas}}</td>
                            <td><a class="btn btn-danger" (click) = "supprimerArticle_cosmetique(element)">Supprimer</a></td>
                            <td><a class="btn btn-success" [routerLink]="['/update-article-cosmetique',element.idarticle]">Modifier</a></td>
                            </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
