import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
@Component({
  selector: 'app-cosmetiques',
  templateUrl: './cosmetiques.component.html'
})
export class CosmetiquesComponent implements OnInit {

  cosmetiques : article[];
  constructor() {
    this.cosmetiques = [
      { idarticle: 1, nomarticle: "SUN SECURE Blur SPF50", prixarticle: 69, dateCreation: new Date("2011-01-14")},
      { idarticle: 2, nomarticle: "SVR sebiaclear gel moussant 200 ml", prixarticle: 30, dateCreation: new Date("2020-12-17")},
      { idarticle: 3, nomarticle: "SVR hydraliane crème hydratante riche 40 Ml", prixarticle: 46, dateCreation: new Date("2020-02-20")}
    ];
    }
  ngOnInit(): void {

  }
}
<html>
    <div class="container">
        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped">
                    <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th> </tr>
                    <thead>
                        <tr>
                            <th>Liste de Produits</th>
                        </tr>
                    </thead>
                    <tbody *ngFor="let element of cosmetiques">
                        <tr >
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
