import { Component, OnInit } from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { Cosmetique} from '../model/Cosmetique.model';
import { CosmetiqueService } from '../services/Cosmetique.service';
import { Classification } from "../model/classification.model";



@Component({
  selector: 'app-add-cosmetique',
  templateUrl: './add-cosmetique.component.html',
  styles: [
  ]
})
export class AddCosmetiqueComponent implements OnInit{

  newArticle = new article();
  Classifications! : Classification[];
  newIdClas! : number;
  newClassification! : Classification;


  constructor(
    private router :Router,
    private cosmetiqueService: CosmetiqueService) { }

    ngOnInit(): void {
      this.articleService.listeclassifications().
      subscribe(clas => {this.Classifications = clas;
      console.log(clas);
      });
      }


  addArticle() {
    this.newArticle.classification = this.Classifications.find(clas => clas.idClas == this.newIdClas)!;
    this.articleService.ajouterArticleCosmetique(this.newArticle).subscribe(arts => {
      console.log(arts);
      this.router.navigate(['cosmetiques']);
    });
  }




}
<html>
    <div class="container">
        <div>
            <h2>Ajouter un produit cosmétique :</h2>
        </div>
        <form >
            <div hidden class="col-sm-2 col-md-2 col-lg-2">
            <label>ID cosmétique</label>
            <input type="text" [(ngModel)]= "newCosmetique.idCosmetique" name="idCosmetique" class="form-control">
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4" >
            <label >Nom cosmétique</label>
            <input type="text" [(ngModel)]= "newCosmetique.nomCosmetique" name="nomCosmetique" class="form-control">
            </div>
            <div class="col-sm-2 col-md-2 col-lg-2">
            <label >Prix cosmétique</label>
            <input type="number" [(ngModel)]= "newCosmetique.prixCosmetique" name="prixCosmetique" class="form-control">
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4">
            <label >Date création</label>
            <input type="date" [(ngModel)]= "newCosmetique.dateCreation" name="dateCreation" class="form-control">
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4">
                <label>Catégorie</label>
                <select class="form-control form-control-lg" id="idCat" name="idCat" [(ngModel)]= "newCategoryId">
                    <option *ngFor= "let category of Categories" [value]= "category.idCat">
                        {{category.nomCat}}
                    </option>
                </select>
            </div> 

            <div class="mt-2">
                <button type="submit" (click)= "addCosmetique()" class="btn btn-success">Ajouter</button>
            </div>
        </form>
    </div>    
</html>
