import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';
import { ArticleService } from '../services/article.service';
import { Classification } from '../model/classification.model';

@Component({
  selector: 'app-recherche-par-nom',
  templateUrl: './recherche-par-nom.component.html'
})
export class RechercheParNomComponent implements OnInit {


  cosmetiques: article[] = [];
  nomarticle: string = '';
  classifications: Classification[] = [];
  searchTerm!:string;
  constructor(private articleService: ArticleService) {}
  ngOnInit(): void {
    //this.classifications = this.articleService.listeclassifications();
    console.log(this.classifications);
  }
  rechercherArticle(){
    this.articleService.rechercherParNom(this.nomarticle).subscribe(arts => {
    this.cosmetiques = arts; 
    console.log(arts)});
    }


}
<html>
    <div class="container">
        <form class="form-inline">
            <div class="form-group mx-sm-3 mb-2 mt-2 ">
                <label>Nom Article_cosmetique</label>
                <input type="text" class="form-control" [(ngModel)]="nomarticle" name="nomarticle" class="form-control">
                <button type="submit" (click)="rechercherArticle()" class="btn btn-primary mb-2 ml-2">Rechercher</button>
            </div>
        </form>

        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped">

                    <thead>
                        <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th><th>Classification</th> </tr>
                    </thead>
                    <tbody *ngFor= "let element of cosmetiques">
                        <tr >
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.classification?.nomClas}}</td>
                           <!--  <td><a *ngIf= "authService.isAdmin()"class="btn btn-danger" (click)="supprimerArticle_cosmetique(element)">Supprimer</a></td>
                            <td><a *ngIf= "authService.isAdmin()" class="btn btn-success" [routerLink]="['/update-article-cosmetique',element.idarticle]">Modifier</a></td> -->
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
