import { Component, OnInit } from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { article } from '../model/article.model';
import { ArticleService } from '../services/article.service';
import { Classification } from "../model/classification.model";



@Component({
  selector: 'app-add-cosmetique',
  templateUrl: './add-cosmetique.component.html',
  styles: [
  ]
})
export class AddCosmetiqueComponent implements OnInit{

  newArticle = new article();
  Classifications! : Classification[];
  newIdClas! : number;
  newClassification! : Classification;


  constructor(
    private router :Router,
    private articleService: ArticleService) { }

    ngOnInit(): void {//this.Classifications= this.articleService.listeclassifications();}

    addArticle()
    {

      console.log(this.newIdClas);
      //this.newClassification=this.articleService.consulterclassification(this.newIdClas);
      this.newArticle.Classification = this.newClassification;
      this.articleService.ajouterArticleCosmetique(this.newArticle);
      this.router.navigate(['cosmetiques']);
    };
}
}
<html>
    <div class="container">
        <div>
            <h2>Ajouter un article :</h2>
        </div>
        <form >
            <div class="col-sm-2 col-md-2 col-lg-2">
            <label >ID article</label>
            <input type="text" [(ngModel)]= "newArticle.idarticle" name="idarticle" class="form-control">
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4" >
            <label >Nom article</label>
            <input type="text" [(ngModel)]= "newArticle.nomarticle" name="nomarticle" class="form-control">
            </div>
            <div class="col-sm-2 col-md-2 col-lg-2">
            <label >Prix article</label>
            <input type="number" [(ngModel)]= "newArticle.prixarticle" name="prixarticle" class="form-control">
            </div>
            <div class="col-sm-4 col-md-4 col-lg-4">
            <label >Date création</label>
            <input type="date" [(ngModel)]= "newArticle.dateCreation" name="dateCreation" class="form-control">
            </div>
            <!-- <div class="col-sm-4 col-md-4 col-lg-4">
                <label>Classification</label>
                <select class="form-control form-control-lg" id="idCat" name="idCat" [(ngModel)]= "newIdClas">
                    <option *ngFor= "let clas of Classifications" [value]= "clas.idClas">
                        {{clas.nomClas}}
                    </option>
                </select>
            </div> -->

            <div class="mt-2">
                <button type="submit" (click)= "addArticle()" class="btn btn-success">Ajouter</button>
            </div>
        </form>
    </div>    
</html>
