import { ArticleService } from './../../../Tp5/src/app/services/article.service';
import { Classification } from './../model/classification.model';
import { Component, OnInit } from '@angular/core';
import { article } from '../model/article.model';

@Component({
  selector: 'app-recherche-par-classtfication',
  templateUrl: './recherche-par-classtfication.component.html',
})
export class RechercheParClasstficationComponent implements OnInit {

  cosmetiques!: article[];
  IdClassification!: number;
  classifications!: Classification[];

  constructor(private articleService: ArticleService) {}

  ngOnInit(): void {
    this.classifications = this.articleService.listeclassifications();
    console.log(this.classifications);
  }

  onChange(): void {
    if (this.IdClassification) {
      this.cosmetiques = this.articleService.rechercheParCategorie(this.IdClassification);
    } else {
      this.cosmetiques = [];
    }
  }
}






<html>
    <div class="container">

        <div class="col-sm-4 col-md-4 col-lg-4 mb-4">
            <label>Categories </label>
            <select class="form-control form-control-lg" id="idCat" name="idCat" [(ngModel)]="IdClassification" (change)="onChange()">
                <option *ngFor="let element of classifications" value="{{element.idClas}}">
                    {{element.nomClas}}
                </option>
            </select>
        </div>

        <div class="card shadow mb-4">
            <div class="card-body">
                <table  class="table table-striped">

                    <thead>
                        <tr> <th>Id</th><th>Nom</th> <th>Prix</th> <th>Date Création</th><th>type</th> </tr>
                    </thead>
                    <tbody *ngFor= "let element of cosmetiques">
                        <tr >
                            <td>{{element.idarticle}}</td>
                            <td>{{element.nomarticle}}</td>
                            <td>{{element.prixarticle}}</td>
                            <td>{{element.dateCreation | date:'dd/MM/yyyy'}}</td>
                            <td>{{element.Classification.nomClas}}</td>
                            <td><a class="btn btn-danger" (click)= "supprimerArticle_cosmetique(element)">Supprimer</a></td>
                            <td><a  class="btn btn-success" [routerLink]="['/update-article-cosmetique',element.idarticle]">Modifier</a></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>
    </div>
</html>
